import { CharsetType } from '../enums';
export default class RandomUtils {
    /**
     * @example
     * // returns 5 | 42 | 99 | ...
     * RandomUtils.getRandomInt(100, 5);
     * @returns {number} Returns random int(x) where x >= min && x < max
     * */
    static getRandomInt(max: number, min?: number): number;
    /**
     * @example
     * // returns 5.1245 | 64 | 99.9999 | ...
     * RandomUtils.getRandomDouble(100, 5);
     * @returns {number} Returns random double(x) where x >= min && x < max
     * */
    static getRandomDouble(max: number, min?: number): number;
    /**
     * @example
     * // returns 'c1bA6' | ...
     * RandomUtils.getRandomString(5);
     * @example
     * // returns '123456789012' | ...
     * RandomUtils.getRandomString(12, CharsetType.Numeric);
     * @returns {string} Returns random string(x) where x.length === length
     */
    static getRandomString(length: number, charsetType?: CharsetType): string;
    /**
     * @returns {boolean} Returns random boolean.
     */
    static getRandomBoolean(): boolean;
}
